import React from 'react'
import {Comment} from './Styled'

export default function CommentItem ({comments, parent}) {
    console.log(comments)
    console.log(parent)

    const Children = comments.filter(comment => comment.parent === parseInt(parent))

    console.log(Children)


    return (
        <div >
            <ul>
                <Comment>{comments[0].id}</Comment>

                {
                    comments.forEach((comment, index) => {
                        if(Children.includes(comment)){
                            <CommentItem key={comment.id} parent={comment.id} comments={comments.slice(index)}/>
                        }
                    })
                }
            </ul>
        </div>
    )
}
