import axios from 'axios'

export function retrieveComments(id, parent){
    axios({
        method: 'get',
        url: `/wp-json/wp/v2/comments/?post=${id}&per_page=10&parent=${parent}`,
        headers: {'Content-Type': 'application/json'}
    })
    .then(res => {
        return res.data
    })
    .catch(err => console.error(err))
}
