import {useState} from 'react'

export default function CommentForm() {

    const [state, setState] = useState({
        name:''
    })

    return (
        <div>
            <label for="name">Name:</label>
            <input type="text" id="name" name="name" onChange={(event) => setState({name: event.target.value})}/>
            <label for="email">Email:</label>
            <input type="text" id="email" name="email" onChange={this.upDateEmail}/>
            <label for="comment">Comment:</label>
            <input type="text" id="comment" name="comment" onChange={this.upDateComment}/>
        </div>
    );
}
