import React from 'react'
import {Comment} from './Styled'

export default function CommentItem ({comments, parent}) {
    console.log(comments)
    console.log(parent)

    const Children = comments.filter(comment => comment.id == parent);

    return (
        <Comment >
            <li>
            <div><p>'id':{comments[0].id}</p>
            <p>'parent':{comments[0].parent}</p></div>
            </li>
            <ul>
                { Children.map((comment, index) => (
                    <CommentItem key={comment.id} parent={comment.id} comments={comments.slice(index)}/>
                ))}
            </ul>
        </Comment>
    )
}
