import React from 'react'
import styled from "styled-components";

const Container = styled.div`
    display:flex;
    flex-direction:column;
    align-items:center;
`   
const InputContainer = styled.div`
    position:relative;
    max-width:300px;
    > input {
        border:2px solid var(--s-3);
        height:32px;
        width:100%;
        border-radius:25px;
        background-color:var(--p-10);
        padding-left:12px;
    }
    > label {
        position:absolute;
        color: black;
        left:12px;
        top: 4px;
        transition: 
        top 200ms ease-in,
        font-size 200ms ease-in;
        background-color:var(--p-10);
        padding:0 4px;
        cursor:text;
        color:var(--p-0);
    }
    > .input:focus ~ label, .input:not(:placeholder-shown).input:not(:focus) ~ label  {
        top: -10px;
        font-size: 12px;
    }
`  
const ValidationHints = styled.div`
    margin-top:.75em;
    p {
        // layout
        position: relative;
        width:200px;

        // looks
        background-color: #fff;
        padding: .2em;
        border-radius: .2rem;
    box-shadow:	0 0.125rem 0.5rem rgba(0, 0, 0, .3), 0 0.0625rem 0.125rem rgba(0, 0, 0, .2);
    }

    p::before {
        // layout
        content: '';
        position: absolute;
        width: 0;
        height: 0;
        bottom: 100%;
        left: 1.5em; // offset should move with padding of parent
        border: .75rem solid transparent;
        border-top: none;

        // looks
        border-bottom-color: #fff;
        filter: drop-shadow(0 -0.0625rem 0.0625rem rgba(0, 0, 0, .1));
    }
`

export default function Input() {

    return (
        <Container>
            <InputContainer>
                <input className='input' name='input' placeholder=" "/>
                <label>
                    label
                </label>
            </InputContainer>
            <ValidationHints>
                <p>nah bruh</p>
            </ValidationHints>
        </Container>
    )
}
