// Import Swiper React components
import { Swiper, SwiperSlide } from 'swiper/react';
import styled from "styled-components";
import {Wrapper} from '../Styled';
import PostSliderItem from './PostSliderItem'

// Import Swiper styles
import 'swiper/swiper-bundle.css';
import 'swiper/components/navigation';

// import Swiper core and required modules
import SwiperCore, {
  Navigation, Pagination
} from 'swiper';

SwiperCore.use([Navigation, Pagination]);

const SwiperWrapper = styled(Wrapper)`
  .swiper{
  }
  .swiper-slide{
    background-color:green;
    margin:1em 0;
    display:flex;
    justify-content:center;
  }
`

export default function PostSlider (props) {

  const [category] = useState(props.category)
  const [posts, setPosts] = useState([])
  const [isLoaded, setIsLoaded] = useState(false)

  useEffect( () => {
      console.log(category)

      retrieveCategoryIdByName(category)
      .then( res => {
          if(typeof res[0] === 'undefined'){

          } else {
              retrievePostsByCategory(res[0].id)
              .then(res => {
                  setPosts(res)
                  setIsLoaded(true)
              })
              .catch(err => console.log(err));
          }
      })


  },[category])

  return (
    <SwiperWrapper>
      <Swiper
        modules={[Navigation, Pagination]}
        navigation
        pagination
        slidesPerView={2}
        onSlideChange={() => console.log('slide change')}
        onSwiper={(swiper) => console.log(swiper)}
      >
        { posts.map(post => (
            <PostSliderItem key={post.id} post={post}/>
        ))}
      </Swiper>
    </SwiperWrapper>
  );
};
