import {useState} from 'react'
import styled from 'styled-components'
import axios from 'axios';
import {Button} from '../Styled'


const Form = styled.form`
    display:flex;
    flex-direction:column;
    padding:1em;
    align-items:center;
`
const Label = styled.label`
`

const Input = styled.input`
    width=100%;
    border: none;
    border-bottom:2px solid var(--primary);
    background-color: var(--primary50);
`

const Info = styled.div`
    display:flex;
    width:100%;
`
const FormItem = styled.div`
    display:flex;
    Flex-Direction:column;
    width:100%;
    margin:.5em;
    overflow:scroll;
`


export default function CommentForm(props) {
    const [parent] = useState(props.parent)
    const [post] = useState(props.post)

    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [comment, setComment] = useState('')

    const handleSubmit = (event) =>{
        event.preventDefault();
        (async () => {
            const comment = JSON.stringify({
                post: post,
                author_name: name,
                author_email: email,
                content:comment,
                parent:parent
              });
            const data = await postComment(comment)
            setComments(data)
            setLoaded(true)
            setUpdated(false)
        })()
      }

        return (
            <Form onSubmit={handleSubmit.bind(this)}>
                <Info>
                    <FormItem>
                        <Label htmlFor="name">Name</Label>
                        <Input type="text" id="name" name="name" value={name} onChange={(event) => {setName(event.target.value)}}/>
                    </FormItem>
                    <FormItem>
                        <Label htmlFor="email">E-mail</Label>
                        <Input type="text" id="email" name="email" value={email} onChange={(event) => {setEmail(event.target.value)}}/>
                    </FormItem>
                </Info>
                <FormItem>
                    <Label htmlFor="comment">Comment</Label>
                    <Input type="text" id="comment" name="comment" value={comment} onChange={(event) => {setComment(event.target.value)}}/>
                </FormItem>
                <Button type="submit" onClick={() => props.updated(true)}>Submit</Button>
            </Form>
        );
    }
