import React, {useEffect, useRef} from 'react'
import Sidebar from '../Nav/Sidebar'
import Welcome from '../Sections/Welcome'
import Resume from '../Sections/Resume'
import Contact from '../Sections/Contact'

import styled from 'styled-components';

const Page = styled.div`
    @media (min-width: 48em) {
        max-width:80em;
    }   
    @media (min-width: 64em) {
        margin-left:auto;
        margin-right:auto;
    }    
`

const Sections = styled.div`
    padding: 4em 0 0 0;
    margin:0;

    @media (min-width: 48em) {
        margin-left:calc(var(--sidebar-width));
        margin-right:calc(var(--sidebar-width));
    }  
    @media (min-width: 64em) {
    }      
`

export default function Home() {

    const data=[
        {
            id:'about',
            label:'About',
        },
        {
            id:'resume',
            label:'Resume',
        },
        {
            id:'contact',
            label:'Contact',
        },
    ]

    const ref1 = useRef(null)
    const ref2 = useRef(null)
    const ref3 = useRef(null)

    const options = {
        root: null,
        threshold: .5,
        rootMargin: "0 0 -100px 0",
    };

    const callbackFunction = (entries) => {
            const [entry] = entries
    }

    useEffect(() => {
        const observer = new IntersectionObserver( callbackFunction, options)
        if(ref1.current) observer.observe(ref1.current)
        if(ref2.current) observer.observe(ref2.current)
        if(ref3.current) observer.observe(ref3.current)

        return() => {
            if(ref1.current) observer.unobserve(ref1.current)
            if(ref2.current) observer.unobserve(ref2.current)
            if(ref3.current) observer.unobserve(ref3.current)
        }
    }, [ref1, ref2, ref3])

    return (
        <Page>
            <Sections>
                <div id='about'><Welcome ref={ref1}/></div>
                <div id='resume'><Resume ref={ref2}/></div>
                <div id='contact'><Contact ref={ref3}/></div>
            </Sections>
            <Sidebar data={data}/>
        </Page>
    )
}
