import React, { useState, useEffect } from 'react';
import { retrievePostsByCategory } from '../../service/WPService';
// Import Swiper React components
import {Wrapper, Loading, Spinner} from '../../components/Styled';
import Slider from '../../components/Slider';
import PostSliderItem from './PostSliderItem'

// Import Swiper styles
import 'swiper/swiper-bundle.css';
import 'swiper/components/navigation';
import 'swiper/components/pagination';


// import Swiper core and required modules
import SwiperCore, {
  Navigation, Pagination
} from 'swiper';

SwiperCore.use([Navigation, Pagination]);

const createPostSliderItems = (posts) => {
    let postSliderItems = [];
    for(let i; i<posts.lenght; i++){
        postSliderItems.push(<PostSliderItem key={posts[i].id} post={posts[i]}/>)
    }
    return postSliderItems;
} 

export default function PostSlider (props) {

  const [posts, setPosts] = useState([])
  const [sliderItems, setSliderItems] = useState([])
  const [isLoaded, setIsLoaded] = useState(false)

  useEffect( () => {

    retrievePostsByCategory(props.category)
    .then(res => {
        setPosts(res)
        setSliderItems(createPostSliderItems(posts))
        setIsLoaded(true)
    })
    .catch(err => console.log(err));
  },[props.category])

  if(isLoaded){
    return (
        <Slider Items={sliderItems}/>
    )
  } else {
  return(               
      <Loading><Spinner/></Loading>
  )
  }
};
