import React, {useState} from 'react'
import styled from 'styled-components'
import CommentForm from './CommentForm'
import Replies from './Replies'

const Comment = styled.div`
    border: 2px solid var(--primary);
    border-radius:1em;
    padding:1em;
    margin:1em;
    display:flex;
    max-width:55em;
    justify-content:space-between;
`

const Avatar = styled.img`
    width: 48px;
    height: 48px;
    border-radius: 50%;

`
const Author = styled.p`

`
const Meta = styled.div`

`
const Content = styled.div`

`
const Text = styled.p`

`
const Date = styled.span`

`

export default function CommentItem (props) {
    const [hideReplies, setHideReplies] = useState(false);
    const [hideForm, setHideForm] = useState(false);

    const [comment] = useState(props.comment.content.rendered)
    const [author] = useState(props.comment.author_name)
    const [avatar] = useState(props.comment.author_avatar_urls[48])
    const [date] = useState(props.comment.date)

    const showReplies = () => {
        setHideReplies(!hideReplies);
        console.log('hideReplies')

    }

    const showForm = () => {
        setHideForm(!hideForm);
        console.log('hideForm')
    }

    return (
        <div>
            <Comment >
                <Meta>
                    <Avatar src={avatar}></Avatar>
                    <Author>{author}</Author>
                </Meta>
                <Content>
                    <Text dangerouslySetInnerHTML={{__html: comment}}></Text>
                    <button onClick={() => showReplies()}>Show replies</button>
                    <button onClick={() => showForm()}>reply</button>
                </Content>
            </Comment>
            <Date>{date}</Date>
            {hideForm && <CommentForm key={'f'+props.comment.id} post={props.comment.post} ></CommentForm>}
            {hideReplies && <Replies key={'r'+props.comment.id} post={props.comment.post} parent={props.comment.id}></Replies>}
        </div>
    )
}
