import {useState} from 'react'
import seperateChildren from './utils'

export default function Comments({comments}) {

    const [state, setState] = useState({
        comments: comments,
        childrenSorted: null,
        parentId: 0,
    })

    const children = seperateChildren(comments);
    setState({
        Childrensorted: children,
    })

    return;
}
