import React, {useState, useEffect} from 'react';
import {useParams} from "react-router-dom";
import axios from 'axios';


function PostPage(props) {
    const [post, setPost] = useState('');
    const [img, setImg] = useState('');

    const params = useParams();

    console.log(params.id);

    useEffect( () => {
        axios(`/wp-json/wp/v2/posts/${params.id}`)
        .then(res => 
            setPost(res.data),
            axios(`/wp-json/wp/v2/media/${post.featured_media}`).then(res => {
                setImg(res.data.media_details.sizes.full.source_url);
            }))
        .catch(err => console.error(err))

    }, [params.id, post.featured_media]);

    console.log(post);

    return (
        <div className="post-page">
            <div className="image">
                <img  alt="warum"/>
            </div>
            <div className="post">
            </div>
            <p>comments</p>
        </div>
    );
  }

export default PostPage
