import React from 'react'
import styled, {keyframes} from "styled-components";

const Container = styled.div`
    height:800px;
    width:800px;
    position:relative;
    font-size:100px;
    --X:133px;
    --Y:35px;
    background-color:white;

    --delay1: 0s;
    --delay2: 2s;
    --delay3: 3s;
`

const fadeInBox = keyframes`
    0% {
        opacity: .5;
        transform:translateY(0em)
    }
    100% {
        opacity: 1;
    }
`;

const fadeInRow = keyframes`
    0% {
        transform:rotateZ(0deg) translateY(0) translateX(0);
    }
    100% {
    }
`;

const Box = styled.div`
    opacity: 0;
    height:.99em;
    width:.99em;
    position:absolute;
    border:solid 1px black;
    background-color:${props => props.color};
    background: radial-gradient(circle,
                                ${props => props.color} 0%, 
                                var(--p-3) 150%);
    animation:${fadeInBox} .2s ease var(${props => props.delay}) forwards;
`

const Box1 = styled(Box)`
    transform:translateY(-1.59em) translateX(-.91em) skew(60deg) rotateX(60deg);
    order:1;
`

const Box2 = styled(Box)`
    transform:translateY(-.92em) translateX(-.24em) skew(30deg) rotateX(30deg);
    order:2;
`

const Box3 = styled(Box)`
    order:3;
`

const Box4 = styled(Box)`
    transform:translateY(.92em) translateX(-.24em) skew(-30deg) rotateX(-30deg);
    order:2;
`
const Box5 = styled(Box)`
    transform:translateY(1.59em) translateX(-.91em) skew(-60deg) rotateX(-60deg);
    order:1;
`

const Row = styled.div`
    top: 50%;
    left: 50%;
    position: absolute;
    animation:${fadeInRow} 2s ease-in-out forwards;
`
const Row1 = styled(Row)`
    transform:rotateZ(0deg) translateY(var(--Y)) translateX(var(--X));
`
const Row2 = styled(Row)`

    transform:rotateZ(-60deg) translateY(var(--Y)) translateX(var(--X));
`

const Row3 = styled(Row)`

    transform:rotateZ(-120deg) translateY(var(--Y)) translateX(var(--X));
`

const Row4 = styled(Row)`

    transform:rotateZ(-180deg) translateY(var(--Y)) translateX(var(--X));
`

const Row5 = styled(Row)`

    transform:rotateZ(-240deg) translateY(var(--Y)) translateX(var(--X));
`

const Row6 = styled(Row)`

    transform:rotateZ(-300deg) translateY(var(--Y)) translateX(var(--X));
`

export default function Figure() {
    return (
        <Container>
            <Row1>
                <Box1 color="var(--s-5)" delay="--delay3"/>
                <Box2 color="var(--s-5)" delay="--delay2"/>
                <Box3 color="var(--s-5)" delay="--delay1"/>
                <Box4 color="var(--s-5)" delay="--delay2"/>
                <Box5 color="var(--s-5)" delay="--delay3"/>
            </Row1>
            <Row2>
                <Box1 color="var(--s-7)" delay="--delay3"/>
                <Box2 color="var(--s-7)" delay="--delay2"/>
                <Box3 color="var(--s-7)" delay="--delay1"/>
                <Box4 color="var(--s-7)" delay="--delay2"/>
                <Box5 color="var(--s-7)" delay="--delay3"/>
            </Row2>
            <Row3>
                <Box1 color="var(--s-5)" delay="--delay3"/>
                <Box2 color="var(--s-5)" delay="--delay2"/>
                <Box3 color="var(--s-5)" delay="--delay1"/>
                <Box4 color="var(--s-5)" delay="--delay2"/>
                <Box5 color="var(--s-5)" delay="--delay3"/>
            </Row3>
            <Row4>
                <Box1 color="var(--s-3)" delay="--delay3"/>
                <Box2 color="var(--s-3)" delay="--delay2"/>
                <Box3 color="var(--s-3)" delay="--delay1"/>
                <Box4 color="var(--s-3)" delay="--delay2"/>
                <Box5 color="var(--s-3)" delay="--delay3"/>
            </Row4>
            <Row5>
                <Box1 color="var(--s-1)" delay="--delay3"/>
                <Box2 color="var(--s-1)" delay="--delay2"/>
                <Box3 color="var(--s-1)" delay="--delay1"/>
                <Box4 color="var(--s-1)" delay="--delay2"/>
                <Box5 color="var(--s-1)" delay="--delay3"/>
            </Row5>
            <Row6>
                <Box1 color="var(--s-3)" delay="--delay3"/>
                <Box2 color="var(--s-3)" delay="--delay2"/>
                <Box3 color="var(--s-3)" delay="--delay1"/>
                <Box4 color="var(--s-3)" delay="--delay2"/>
                <Box5 color="var(--s-3)" delay="--delay3"/>
            </Row6>
        </Container>
    )
}
