import React, {useState, useEffect} from 'react';
import {Wrapper, Loading, Line} from '../Styled'
import styled from 'styled-components'
import Comments from './Comments/Comments'
import { retrieveFeaturedMedia, retrievePost } from '../../service/WPService';

const PostWrapper = styled(Wrapper)`
    max-width:50em;
    display:flex;
    flex-direction:column;
    @media (max-width: 50em) {
        margin:0;
    }
    border: 1px var(--p-7) solid;
`

const Header = styled.div`
    background: url(${props => props.img}) no-repeat center center fixed;
    border: 1px var(--p-7) solid; 
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
    background-attachment: scroll;
    top: 0;
    bottom: 0;
    left: 50%;
    right: auto;
    position:fixed;
    height:calc(70vh);
    width:100vw;
    max-width:50em;
    z-index:1;
    margin-left:-25em;
    @media (max-width: 52em) {
        margin-left:0;
        left:0;
    }
`

const Margin = styled.div`
    z-index:2;
    background-color: var(--p-9);
`

const Article = styled.article`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    z-index:2;
    background-color:var(--p-9);
`

const InfoSection = styled.div`
    display:flex;
    justify-content: center;
    align-items: flex-end;
    max-width:100%;
    max-height:100%;
    height: calc(100vh - 5em);
    width: 100vw;
    z-index:2;
`

const Info = styled.div`
    height:30vh;
    width:100%;
    background-color:var(--p-9);
    padding:2em;
    display:flex;
    flex-direction:column;
    justify-content:flex-start;
`

const Title = styled.h1`
    color:var(--p-1);
`

const Content = styled.div`
    width:100vw;
    max-width:100%;
    padding:2em;
    display:flex;
    flex-direction:column;
    p{
        color:var(--p-1);
        text-align: justify;
    }
    h2{
        color:var(--p-1);
        text-align: left;
        margin:1em;
    }
    img{
        height:auto;
        max-height:calc(80vh);
        width:auto;
        max-width:100%;
        object-fit: cover;
        display:block;
    }
`

const Date = styled.span`
    color:var(--p-1);
    font-size:.8em;
    align-self:flex-start;
`

const H1 = styled.h1`
    text-align: left;
    align-self: flex-start;
    margin-left:1em;
    color: var(--p-1);
`
export default function PostPage ({match}) {
    const [post, setPost] = useState('');
    const [imageUrl, setImageUrl] = useState('');
    const [isLoaded, setIsLoaded] = useState(false);
    const [depth] = useState(0);

    useEffect( () => {

        const { id } = match.params;
        retrievePost(id)
        .then( res =>{
            setPost(res)
            return retrieveFeaturedMedia(res.featured_media)
        })
        .then(res => {
            if(!(res === null)){
                setImageUrl(res.media_details.sizes.full.source_url)
            }
            setIsLoaded(true)
        })

    },[match.params])

    if(isLoaded){
        return(
            <PostWrapper>
                <Header img={imageUrl}>
                </Header>
                <InfoSection>
                    <Info>
                        <Line height="2px"/>
                        <Title dangerouslySetInnerHTML={{__html: post.title.rendered}}/>
                        <Line height="1px"/>
                        <Date dangerouslySetInnerHTML={{__html: post.date}}/>
                        <Line height="2px"/>
                    </Info>
                </InfoSection>
                <Article>
                    <Content dangerouslySetInnerHTML={{__html: post.content.rendered}}/>
                    <Line height="2px"/>
                    <H1>Comments</H1>
                </Article>
                <Margin>
                    <Comments parent="0" post={post.id} depth={depth}/>
                </Margin>
            </PostWrapper>
        )
    } else {
        return (
            <Loading>Loading...</Loading>
        )
    }
}
