import React, {useState, useEffect} from 'react'
import styled from 'styled-components'
import CommentForm from './CommentForm'
import Comments from './Comments'

const Container = styled.div`
    margin-top:1em;
    display:flex;
`

const Comment = styled.div`
    display:flex;
    flex-direction:column;
    width:100%;
`
const Meta = styled.div`
    display:flex;
    flex-direction:column;
    align-items:center;
    margin-right:1em;
`
const Content = styled.div`
    border: 2px solid var(--primary);
    border-radius:1em;
    padding:.5em;
    display:flex;
    flex-direction:column;
    width:100%;
`

const Avatar = styled.img`
    width: 48px;
    height: 48px;
    border-radius: 50%;

`
const Author = styled.p`

`
const Text = styled.p`

`
const Date = styled.div`
    p{
        font-size:.6em;
        color:var(--primary)
    }
`

export default function CommentItem (props) {
    const [hideReplies] = useState(false);
    const [hideForm] = useState(false);

    const [comment] = useState(props.comment.content.rendered)
    const [author] = useState(props.comment.author_name)
    const [avatar] = useState(props.comment.author_avatar_urls[48])
    const [date] = useState(props.comment.date)

    const [updated, setUpdated] = useState(false);

    useEffect( () => {
        setUpdated(false)
        console.log('update')
    }, [updated])

    return (
        <Container>
            <Meta>
                <Avatar src={avatar}></Avatar>
                <Author>{author}</Author>
            </Meta>
            <Comment>
                <Content>
                    <Text dangerouslySetInnerHTML={{__html: comment}}></Text>
                    <Date><p>{date}</p></Date>
                </Content>
                {!hideForm && 
                    <CommentForm 
                        updated={a => setUpdated(a)} 
                        parent={props.comment.id} 
                        key={'f'+props.comment.id} 
                        post={props.comment.post}                    
                    ></CommentForm>
                }
                <Comments reply={'reply'} key={'r'+props.comment.id} post={props.comment.post} parent={props.comment.id}></Comments>
            </Comment>
        </Container>
    )
}
