import {useState} from 'react'
import styled from 'styled-components'
import {Button} from '../Styled'
import {postComment} from '../../service/WPService'

const Form = styled.form`
    display:flex;
    flex-direction:column;
    padding:1em;
    align-items:center;
    background-color:var(--p);
`
const Label = styled.label`
color:white;
`

const Input = styled.input`
    width=100%;
    border: none;
    border-bottom:2px solid var(--primary);
    background-color: white;
    border-radius:1em;
    padding:.5em;
`

const Comment = styled.textarea`
    height: 150px;
    width=100%;
    border: none;
    border-bottom:2px solid var(--primary);
    background-color: white;
    border-radius:1em;
    padding:.5em;
`

const Info = styled.div`
    display:flex;
    width:100%;
`
const FormItem = styled.div`
    display:flex;
    Flex-Direction:column;
    width:100%;
    margin:.5em;
    overflow:scroll;
`


export default function CommentForm(props) {
    const [parent] = useState(props.parent)
    const [post] = useState(props.post)
    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [comment, setComment] = useState('')

    const handleSubmit = (event) =>{
        event.preventDefault();
        const data = JSON.stringify({
            post: post,
            author_name: name,
            author_email: email,
            content:comment,
            parent:parent
        })
        postComment(data)
      }

        return (
            <Form onSubmit={handleSubmit.bind(this)}>

                <FormItem>
                    <Label htmlFor="comment">Comment</Label>
                    <Comment type="text" id="comment" name="comment" value={comment} onChange={(event) => {setComment(event.target.value)}}/>
                </FormItem>
                <Button type="submit" onClick={() => props.updated(true)}>Submit</Button>
            </Form>
        );
    }
